mapper.select:
select
module_id, parent_id, parent_name, name, is_leaf, ico, cpermission, curl, ctype, state, belong, remark, order_no
from db_heima_mm.ss_module


        and module_id = #{moduleId}


        and parent_id = #{parentId}


        and parent_name = #{parentName}


        and name = #{name}


        and is_leaf = #{isLeaf}


        and ico = #{ico}


        and cpermission = #{cpermission}


        and curl = #{curl}


        and ctype = #{ctype}


        and state = #{state}


        and belong = #{belong}


        and remark = #{remark}


        and order_no = #{orderNo}
