mapper.select:
select
role_id, name, remark, order_no, create_by, create_dept, create_time, update_by, update_time
from db_heima_mm.ss_role


        and role_id = #{roleId}


        and name = #{name}


        and remark = #{remark}


        and order_no = #{orderNo}


        and create_by = #{createBy}


        and create_dept = #{createDept}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
